func AesDecryptData(key, data string) (res string, err error) {
	eData, err := base64.URLEncoding.DecodeString(data)
	if err != nil {
		return
	}
	context := encrypt.AesDecryptECB(eData, []byte(key))
	res = string(context)
	return
}

