func spiralOrder(matrix [][]int) []int {
    if len(matrix) == 0 || len(matrix[0]) == 0{
        return []int{}
    }

    res := []int{}
    l := 0
    r := len(matrix[0]) - 1
    t := 0
    b :=  len(matrix) - 1

    for {
        // å·¦åˆ°å³
        for i := l ; i <= r; i++ {
            res = append(res, matrix[t][i])
        }
        t++
        if t > b {
           break 
        }

        // å³åˆ°ä¸‹
        for i := t; i <= b; i++ {
            res = append(res, matrix[i][r])
        }

        r--
        if l > r {
            break
        }

        // ä¸‹åˆ°å·¦
        for i := r; i >= l; i-- {
            res = append(res, matrix[b][i])
        }
        b--
        if t > b {
           break 
        }


        // å·¦åˆ°ä¸Š
        for i := b; i >= t; i-- {
            res = append(res, matrix[i][l])
        }
        l++
        if l > r {
            break
        }
    }

    return res
}
```



