func getIntersectionNode(headA, headB *ListNode) *ListNode {
    pos1 := headA
    pos2 := headB
    for pos1 != pos2 {
        if pos1 != nil{
            pos1 = pos1.Next
        }else{
            pos1 = headB
        }

        if pos2 != nil {
            pos2 = pos2.Next
        }else{
            pos2 = headA
        }
    }
    return pos2
}   
