```go
func romanToInt(s string) int {
  res := map[string]int{
        "I": 1,
        "V": 5,
        "X": 10,
        "L": 50,
        "C": 100,
        "D": 500,
        "M": 1000,
    }
    sum := 0
    for i := 0; i < len(s); i++ {
        val := res[string(s[i])]
        if i < len(s)-1 && val < res[string(s[i+1])] {
            sum -= val
        } else {
            sum += val
        }
    }
    return sum
}
```



