        console.log(this.x);// undefined
    }
}

var fn1 = obj.fn;
fn1();//window.fn1();

```



+ 需要注意的一种情况是下面的 f()函数, 此时 f()只是一个普通函数

```javascript
var obj = {
	x : 10,
	fn : function(){

        this;// 此处是 obj

		function f(){
			console.log(this); // window
			console.log(this.x);// undefined
		}
		f();

	}
}

obj.fn();
```



### 三: 函数用 call 或 apply 调用

+ 函数调用 call

```javascript
var obj = {
    x : 10
};

var fn = function(){
    console.log(this);  //函数调用call, this指向call中的参数, 就是obj
    console.log(this.x);// 10
}

fn.call(obj)
