package main

import "fmt"

func search(nums []int, target int) int {
	left := 0
	right := len(nums) - 1

	for left <= right { // 这里是小于等于
		mid := (left + right) / 2
		if nums[mid] > target {
			right = mid - 1
		} else if nums[mid] < target {
			left = mid + 1
		} else {
			return mid
		}
	}
	return -1 // 找不到 return -1
}

func main() {
	arr := []int{1, 3, 11, 150, 201, 800}
	fmt.Println(search(arr, 11)) // 2(下标)
}

